li.OKResult:

local N1 = world.DayCount
if N1 <= 300 then
	me:AddMsg("[NAME]来到殿前，看到一个道骨仙风的老头，正是星象峰第74代掌门。上前微微作揖。");
	me:TriggerStory("XXFBF_01_1");
elseif N1 <= me:RandomInt(350,700) then
	me:AddMsg("[NAME]来到殿前，当任掌门，微微一愣，居然已经不是上次见到的那个掌门了？不知门中发生了什么。");
	me:TriggerStory("XXFBF_01_1");
elseif N1 <= 1000 then
	me:AddMsg("[NAME]来到殿前，看到一名奇伟男子，双目绽放着炯炯神光，一身紫色长袍修饰着其身躯。");
	me:TriggerStory("XXFBF_01_1");
end

local N1 = world.DayCount
	if N1 <= 300 then
		me:AddMsg("[NAME]来到侧殿，看到几个长老似乎在讨论什么事儿，见到[NAME]进来后，纷纷露出皮笑肉不笑的诡异表情。");
		me:TriggerStory("XXFBF_02_1");
	elseif N1 <= me:RandomInt(350,700) then
		me:AddMsg("[NAME]来到侧殿，发现这些长老似乎比之前那些人更年轻些，只不过修为参差不齐。");
		me:TriggerStory("XXFBF_02_2");
	elseif N1 <= 1000 then
		me:AddMsg("[NAME]来到侧殿，侧殿中的长老们各个闭目养神，眼观鼻，鼻观心。对[NAME]的到来并不是很理会。");
		me:TriggerStory("XXFBF_02_1");
	end

local N1 = me:RandomInt(0,100)
if N1 <= 70 then
	me:AddMsg("[NAME]来到星象峰的练武场，弟子们正在操练着星玄之术，只不过现在的天道，对于该术法要求越来越高，炼成的寥寥无几。");
	me:TriggerStory("XXFBF_03_1");
elseif N1 <= 50 then
	me:AddMsg("[NAME]来到星象峰洗星池边上，远远便听到咿咿呀呀的痛苦叫喊，甚至听到了个别弟子痛哭。");
	me:TriggerStory("XXFBF_03_1");
elseif N1 <= 30 then
	me:AddMsg("[NAME]来到蕴星洞前，这里的弟子围着洞口正在打量着什么。");
	me:TriggerStory("XXFBF_03_1");
elseif N1 <= 10 then
	me:AddMsg("[NAME]来到星门前，有几名弟子正在扫着落叶。");
	me:TriggerStory("XXFBF_03_1");
else
	me:AddMsg("[NAME]似乎运气不好，四下空无一人，吃了个闭门羹。");
end
